import axios from "axios";
import { useState } from "react";

const Weather = () => {
    const[city,setCity]=useState();
    const [weather,setWeather]= useState();
    const handleCityChange= (event)=>{
        setCity(event.target.value);
    }
    const fetchWeather = async () => {
        try{
            const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${'d161d7b1750ff840a501ba6cca02dd15'}`) // API Call
            const kelvinTemp = response.data.main.temp; // Get temperature in Kelvin
            const celsiusTemp = kelvinTemp - 273.15; // Convert Kelvin to Celsius
            const roundedCelsiusTemp = Math.round(celsiusTemp); // Round to nearest whole number
            setWeather({ ...response.data, celsiusTemp: roundedCelsiusTemp });

        }
        catch(error){
            console.log("Error fetching weather data", error)
        }
    }
    const handleClick = (event) => {
        fetchWeather();
    }

    /*const kelvin = weather.data.main.temp;
    const celsius = kelvin - 273.15;
    const FinalCelsius = Math.round(celsius); */
    return ( 
        <div className="weather-container">
            <input type="text" placeholder="Enter City Name" value={city} onChange={handleCityChange} />
            <button onClick={handleClick}>Get Weather</button>
            {weather && <>
            <div className="weather-info">
                <h2>{weather.data.name}</h2>
                <p>Temp is {weather.celsiusTemp} Â°C</p>
                <p>{weather.data.weather[0].description}</p>
            </div>
            </>}
        </div>

     );
}

export default Weather;
